import React, { useState } from 'react';
import axios from 'axios';
import './PersonForm.css'; // Importa el archivo CSS

const PersonForm = () => {
  const [formData, setFormData] = useState({
    numero_identificacion: '',
    tipo_identificacion: '',
    nombre1: '',
    nombre2: '',
    apellido1: '',
    apellido2: '',
    sexo: '',
    fecha_nacimiento: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://localhost:8000/people/${formData.numero_identificacion}`, formData);
      alert('Person updated successfully: ' + JSON.stringify(response.data));
    } catch (error) {
      console.error('Error updating person:', error);
      alert('Error updating person: ' + error.message);
    }
  };

  return (
    <form className="person-form" onSubmit={handleSubmit}>
      <h2>Update Person Information</h2>
      <div className="form-group">
        <label>Identification Number</label>
        <input
          type="text"
          name="Identification number "
          value={formData.numero_identificacion}
          onChange={handleChange}
          placeholder="Enter ID Number"
        />
      </div>
      <div className="form-group">
        <label>Identification Type</label>
        <input
          type="text"
          name="Identification Type"
          value={formData.tipo_identificacion}
          onChange={handleChange}
          placeholder="Enter ID Type"
        />
      </div>
      <div className="form-group">
        <label>First Name 1</label>
        <input
          type="text"
          name="First name 1"
          value={formData.nombre1}
          onChange={handleChange}
          placeholder="Enter First Name"
        />
      </div>
      <div className="form-group">
        <label>First Name 2</label>
        <input
          type="text"
          name="First name 2"
          value={formData.nombre2}
          onChange={handleChange}
          placeholder="Enter Middle Name"
        />
      </div>
      <div className="form-group">
        <label>Last Name 1</label>
        <input
          type="text"
          name="last_name1"
          value={formData.apellido1}
          onChange={handleChange}
          placeholder="Enter Last Name"
        />
      </div>
      <div className="form-group">
        <label>Last Name 2</label>
        <input
          type="text"
          name="last_name2"
          value={formData.apellido2}
          onChange={handleChange}
          placeholder="Enter Second Last Name"
        />
      </div>
      <div className="form-group">
        <label>sexo</label>
        <input
          type="text"
          name="gender"
          value={formData.sexo}
          onChange={handleChange}
          placeholder="Enter gender"
        />
      </div>
      <div className="form-group">
        <label>Date of Birth</label>
        <input
          type="date"
          name="date"
          value={formData.fecha_nacimiento}
          onChange={handleChange}
        />
      </div>
      <button className="submit-button" type="submit">Update Person</button>
    </form>
  );
};

export default PersonForm;
