import React, { useState, useEffect } from 'react';
import './crudApp.css';

const CrudApp = () => {
  const [items, setItems] = useState([]);
  const [formData, setFormData] = useState({
    id: '',
    tipoIdentificacion: '',
    numeroIdentificacion: '',
    nombre1: '',
    nombre2: '',
    apellido1: '',
    apellido2: '',
    sexo: '',
    fechaNacimiento: ''
  });
  const [editingIndex, setEditingIndex] = useState(null);

  // Buscar elementos cuando el componente se monta
  useEffect(() => {
    fetch('http://localhost:3001/api/people')
      .then(response => response.json())
      .then(data => setItems(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleAddItem = () => {
    if (Object.values(formData).some(value => value.trim() === '')) return;

    if (editingIndex !== null) {
      // Actualizar elemento existente
      fetch(`http://localhost:3001/api/people/${formData.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      })
        .then(() => {
          const updatedItems = items.map((item, index) =>
            index === editingIndex ? formData : item
          );
          setItems(updatedItems);
          setEditingIndex(null);
        })
        .catch(error => console.error('Error updating data:', error));
    } else {
      // Agregar nuevo elemento
      fetch('http://localhost:3001/api/people', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      })
        .then(response => response.json())
        .then(data => {
          setItems([...items, { ...formData, id: data.id }]);
        })
        .catch(error => console.error('Error adding data:', error));
    }

    // Limpiar el formulario después de agregar/editar
    setFormData({
      id: '',
      tipoIdentificacion: '',
      numeroIdentificacion: '',
      nombre1: '',
      nombre2: '',
      apellido1: '',
      apellido2: '',
      sexo: '',
      fechaNacimiento: ''
    });
  };

  const handleEditItem = (index) => {
    // Permitir la edición seleccionando un elemento de la lista
    setFormData(items[index]);
    setEditingIndex(index);
  };

  const handleDeleteItem = (index) => {
    // Eliminar el elemento seleccionado
    fetch(`http://localhost:3001/api/people/${items[index].id}`, {
      method: 'DELETE',
    })
      .then(() => {
        const updatedItems = items.filter((_, i) => i !== index);
        setItems(updatedItems);
      })
      .catch(error => console.error('Error deleting data:', error));
  };

  return (
    <div className="container">
      <h1>CRUD React - Editar Elementos</h1>
      <form>
        <input
          type="text"
          name="tipoIdentificacion"
          placeholder="Tipo de Identificación"
          value={formData.tipoIdentificacion}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="numeroIdentificacion"
          placeholder="Número de Identificación"
          value={formData.numeroIdentificacion}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="nombre1"
          placeholder="Primer Nombre"
          value={formData.nombre1}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="nombre2"
          placeholder="Segundo Nombre"
          value={formData.nombre2}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="apellido1"
          placeholder="Primer Apellido"
          value={formData.apellido1}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="apellido2"
          placeholder="Segundo Apellido"
          value={formData.apellido2}
          onChange={handleInputChange}
        />

        <select
          name="sexo"
          value={formData.sexo}
          onChange={handleInputChange}
        >
          <option value="" disabled>Selecciona el Sexo</option>
          <option value="Masculino">Masculino</option>
          <option value="Femenino">Femenino</option>
          <option value="Otro">Otro</option>
        </select>

        <input
          type="date"
          name="fechaNacimiento"
          value={formData.fechaNacimiento}
          onChange={handleInputChange}
        />

        <button type="button" onClick={handleAddItem}>
          {editingIndex !== null ? 'Actualizar' : 'Agregar'}
        </button>
      </form>

      <ul>
        {items.map((item, index) => (
          <li key={index}>
            <div>
              <strong>ID:</strong> {item.id}, <strong>Tipo ID:</strong> {item.identification_type}, <strong>Número ID:</strong> {item.identification_number}, <strong>Nombre Completo:</strong> {`${item.first_name1} ${item.first_name2} ${item.last_name1} ${item.last_name2}`}, <strong>Sexo:</strong> {item.gender}, <strong>Fecha de Nacimiento:</strong> {item.date_of_birth}
            </div>
            <div>
              <button onClick={() => handleEditItem(index)}>Editar</button>
              <button onClick={() => handleDeleteItem(index)}>Eliminar</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CrudApp;
