import React, { useState, useEffect } from 'react';
import './crudApp.css';

const CrudApp = () => {
  const [items, setItems] = useState([]);
  const [formData, setFormData] = useState({
    id: '',
    tipoIdentificacion: '',
    numeroIdentificacion: '',
    nombre1: '',
    nombre2: '',
    apellido1: '',
    apellido2: '',
    sexo: '',
    fechaNacimiento: ''
  });
  const [editingIndex, setEditingIndex] = useState(null);

  useEffect(() => {
    fetch('/api/people')
      .then(response => response.json())
      .then(data => setItems(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleAddItem = () => {
    if (Object.values(formData).some(value => value.trim() === '')) return;

    if (editingIndex !== null) {
      fetch(`/api/people/${items[editingIndex].id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      })
        .then(() => {
          const updatedItems = items.map((item, index) =>
            index === editingIndex ? formData : item
          );
          setItems(updatedItems);
          setEditingIndex(null);
        })
        .catch(error => console.error('Error updating data:', error));
    } else {
      fetch('/api/people', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      })
        .then(() => {
          setItems([...items, formData]);
        })
        .catch(error => console.error('Error adding data:', error));
    }

    setFormData({
      id: '',
      tipoIdentificacion: '',
      numeroIdentificacion: '',
      nombre1: '',
      nombre2: '',
      apellido1: '',
      apellido2: '',
      sexo: '',
      fechaNacimiento: ''
    });
  };

  const handleEditItem = (index) => {
    setFormData(items[index]);
    setEditingIndex(index);
  };

  const handleDeleteItem = (index) => {
    fetch(`/api/people/${items[index].id}`, {
      method: 'DELETE',
    })
      .then(() => {
        const updatedItems = items.filter((_, i) => i !== index);
        setItems(updatedItems);
      })
      .catch(error => console.error('Error deleting data:', error));
  };

  return (
    <div className="container">
      <h1>CRUD REACT READ & UPDATE</h1>
      <input
        type="text"
        name="id"
        placeholder="ID"
        value={formData.id}
        onChange={handleInputChange}
        disabled
      />
      {/* ... otros inputs */}
      <button onClick={handleAddItem}>
        {editingIndex !== null ? 'Actualizar' : 'Agregar'}
      </button>

      <ul>
        {items.map((item, index) => (
          <li key={index}>
            {/* Mostrar los datos */}
            <div>
              <button onClick={() => handleEditItem(index)}>Editar</button>
              <button onClick={() => handleDeleteItem(index)}>Eliminar</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CrudApp;
