import React, { useState } from 'react';
import axios from 'axios';
import './PersonForm.css'; // Importa el archivo CSS

const PersonForm = () => {
  const [formData, setFormData] = useState({
    identification_number: '',
    identification_type: '',
    first_name1: '',
    first_name2: '',
    last_name1: '',
    last_name2: '',
    gender: '',
    date_of_birth: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://localhost:8000/people/${formData.identification_number}`, formData);
      alert('Person updated successfully: ' + JSON.stringify(response.data));
    } catch (error) {
      console.error('Error updating person:', error);
      alert('Error updating person: ' + error.message);
    }
  };

  return (
    <form className="person-form" onSubmit={handleSubmit}>
      <h2>Update Person Information</h2>
      <div className="form-group">
        <label>Identification Number</label>
        <input
          type="text"
          name="identification_number"
          value={formData.identification_number}
          onChange={handleChange}
          placeholder="Enter ID Number"
        />
      </div>
      <div className="form-group">
        <label>Identification Type</label>
        <input
          type="text"
          name="identification_type"
          value={formData.identification_type}
          onChange={handleChange}
          placeholder="Enter ID Type"
        />
      </div>
      <div className="form-group">
        <label>First Name 1</label>
        <input
          type="text"
          name="first_name1"
          value={formData.first_name1}
          onChange={handleChange}
          placeholder="Enter First Name"
        />
      </div>
      <div className="form-group">
        <label>First Name 2</label>
        <input
          type="text"
          name="first_name2"
          value={formData.first_name2}
          onChange={handleChange}
          placeholder="Enter Middle Name"
        />
      </div>
      <div className="form-group">
        <label>Last Name 1</label>
        <input
          type="text"
          name="last_name1"
          value={formData.last_name1}
          onChange={handleChange}
          placeholder="Enter Last Name"
        />
      </div>
      <div className="form-group">
        <label>Last Name 2</label>
        <input
          type="text"
          name="last_name2"
          value={formData.last_name2}
          onChange={handleChange}
          placeholder="Enter Second Last Name"
        />
      </div>
      <div className="form-group">
        <label>Gender</label>
        <input
          type="text"
          name="gender"
          value={formData.gender}
          onChange={handleChange}
          placeholder="Enter Gender"
        />
      </div>
      <div className="form-group">
        <label>Date of Birth</label>
        <input
          type="date"
          name="date_of_birth"
          value={formData.date_of_birth}
          onChange={handleChange}
        />
      </div>
      <button className="submit-button" type="submit">Update Person</button>
    </form>
  );
};

export default PersonForm;
